def search_tag_by_length(theArray,nb,expr)
    j_tag = []
    #reg = /^(\w{#{nb}})$/ # \w cherche A-Z a-z 0-9 # {x}  x caractere # ^ du debut # $ à la fin
    reg = expr
    #debug: puts reg # affiche le regex 
    theArray.each { |j|
        nj = j.gsub("@","") # nj ( name journalist => without @ )
        ok = nj.match?reg # verif si nj match  reg
        if ok == true
            j_tag << j # add journalist shortest name
        end
        }
    #debug: 
    #puts j_tag
    return j_tag,j_tag.length
end

def shortest_tag(theArray)
    for nb in 1..5
        rep = search_tag_by_length(theArray,nb,/^(\w{#{nb}})$/ )
        if rep[0] != []
            break
        end
    end
    return rep[0]
end

def short2long_tag(theArray)
    repAll=[]
    for nb in 1..50
        theArray.each { |j|
        reg = /^(\w{#{nb}})$/
        nj = j.gsub("@","")
        ok = nj.match?reg # verif si nj match  reg
        if ok == true
            repAll << j # add journalist shortest name
        end
        #puts repAll
        #puts "nb:#{nb} all:#{repAll[nb]}" #.length
        }
    
    end
    return repAll
end

def nbCaractere(journalist)
    carLength=[]
    journalist.length.times do |i|
        if carLength[ ( journalist[i].length - 1 ) ] == nil # -1 car $ ne compte pas #init et increment du nb de caractere
            carLength[ ( journalist[i].length - 1 ) ] = 1
        else carLength[ ( journalist[i].length - 1 ) ] += 1
        end
    end

    carLength.length.times do |i| #traite le retour terminal
        if carLength[i] == nil 
            puts " => 0 occurence avec #{i} caractere(s)"
        else
            puts " => #{carLength[i]} occurence(s) avec #{i} caracteres"
        end
    end
end



